 void Font::set_type(Font::Type id){
	switch (id)
	{
	case Font::SMALL:
		font.data_ = &font_6x6[0];
		font.height_ = 6;
		font.table_width_ = 7;
		font.first_char_ = ' ';
		font.last_char_ = Font::NUM_OF_GLYPHS - 1;
		break;
	case Font::LARGE:
		font.data_ = &font_8x8[0];
		font.height_ = 8;
		font.table_width_ = 9;
		font.first_char_ = ' ';
		font.last_char_ = '~';
		break;
	default:
		break;
	}
}
